var now = new Date();

function createtime() {
    var start = new Date("09/12/2024 00:00:00");
    now.setTime(now.getTime() + 250);

    var elapsedHours = (now - start) / 1000 / 60 / 60;
    var hours = Math.floor(elapsedHours % 24); // 获取小时数
    hours = hours < 10 ? "0" + hours : hours;

    let content = "";
    if (hours < 18 && hours >= 9) {
        content = `
            <img class='boardsign' src='/img/badashou.svg' title='距离月入25k也就还差一个大佬带我~'>
            <span class='textTip'><br>本站正在愉快地运行中！</span>
            <i class='fas fa-heartbeat' style='color:red'></i>`;
    } else {
        content = `
            <img class='boardsign' src='/img/xiwanghuizhan.svg' title='下班了就该开开心心的玩耍，嘿嘿~'>
            <span class='textTip'><br>本站正在愉快地运行中！</span>
            <i class='fas fa-heartbeat' style='color:red'></i>`;
    }

    // 更新页面内容
    const workboard = document.getElementById("workboard");
    if (workboard) {
        workboard.innerHTML = content;
    }
}

// 每250ms更新一次内容
setInterval(() => {
    createtime();
}, 250);